/**
 * Register options
 */

function dslc_plugin_options_init() {

	global $dslc_plugin_options;

	/**
	 * Add Sections
	 */

	foreach ( $dslc_plugin_options as $section_ID => $section ) {

		add_settings_section(
			$section_ID,
			$section['title'],
			'dslc_plugin_options_display_options',
			$section_ID
		);

		register_setting(
			$section_ID,
			$section_ID
		);

	}

	/**
	 * Add Fields
	 */

	foreach ( $dslc_plugin_options as $section_ID => $section ) {

		foreach ( $section['options'] as $option_ID => $option ) {

			$option['id'] = $option_ID;

			if ( ! isset( $option['section'] ) ) {

				$option['section'] = $section_ID;
			}

			$option['name'] = 'dslc_plugin_options[' . $option['id'] . ']';

			$value = '';
			$options = get_option( 'dslc_plugin_options' );

			if ( isset( $options[$option_ID] ) ) {

				$value = $options[$option_ID];
			}

			/// Prev version struct
			if ( $value == '' ) {

				$options = get_option( $section_ID );

				if ( isset( $options[$option_ID] ) ) {

					$value = $options[$option_ID];
				}

				if ( $value == '' ) {

					$value = $option['std'];
				}
			}

			$option['value'] = $value;
			$function = 'dslc_plugin_option_display_' . $option['type'] . '( "' . base64_encode( serialize( $option ) ) . '" );';

			add_settings_field(
				$option_ID,
				$option['label'],
				create_function( '$a', $function ),
				$section_ID,
				$section_ID
			);
		}

	}


} add_action( 'admin_init', 'dslc_plugin_options_init' );

